mapper.insert:
INSERT INTO 
    ARTICLE ( USER_NO
			, PLANT_NO
			, ARTICLE_TITLE
			, ARTICLE_SUBJECT
			, ARTICLE_CONTENT
			, ARTICLE_PRODUCT_PRICE	) 			
	VALUES (  #{userNo}
			, #{plantNo}
			, #{articleTitle}
			, #{articleSubject}
			, #{articleContent}
			, #{articleProductPrice} )

mapper.update:
		UPDATE 	ARTICLE 
		   SET 	ARTICLE_TITLE 			= #{articleTitle}
		   	  , ARTICLE_SUBJECT			= #{articleSubject}
		   	  , ARTICLE_CONTENT			= #{articleContent}
		   	  , PLANT_NO				= #{plantNo}
		   	  , ARTICLE_PRODUCT_PRICE 	= #{articleProductPrice}
    	 WHERE 	USER_NO 				= #{userNo} 
    	   AND  ARTICLE_NO 				= #{articleNo} 
    	   AND  ARTICLE_ACTIVE			= 1

		UPDATE 	ARTICLE 
		   SET 	ARTICLE_ACTIVE 		= 0 
    	 WHERE 	USER_NO = #{userNo} AND ARTICLE_NO = #{articleNo}
