mapper.insert:
INSERT INTO 
  PLANT_SR (  PLANT_NO
  			, WH_PLANT_NO
			, SR_DHT_NO
			, SR_PR_NO
			, SR_WT_NO
			, PLANT_SR_HUMI
			, PLANT_SR_TEMP
			, PLANT_SR_LIGHT
			, PLANT_SR_WT	) 			
	VALUES (  #{plantNo}
			, #{warehousePlantNo}
			, #{dhtNo}
			, #{photoRegistorNo}
			, #{waterTempNo}
			, #{humi}
			, #{temp}
			, #{light}
			, #{waterTemp} 			)

INSERT INTO 
    SR_CTRL ( USER_NO
  			, WH_PLANT_NO
			, SR_WP_NO
			, SR_HUMD_NO
			, SR_WP_TF
			, SR_HUMD_TF		) 			
	VALUES (  #{userNo}
			, #{warehousePlantNo}
			, #{waterPumpNo}
			, #{humidifierNo}
			, #{waterPumpTf}
			, #{humidifierTf}	)

mapper.update:
UPDATE SR_CTRL 
   SET SR_WP_TF = #{waterPumpTf} 		
 WHERE USER_NO = #{userNo}

UPDATE SR_CTRL 
   SET SR_HUMD_TF = #{humidifierTf} 		
 WHERE USER_NO = #{userNo}
