mapper.insert:
INSERT INTO 
    PRODUCT ( USER_NO
			, PLANT_NO
			, PRODUCT_NAME
			, PRODUCT_TYPE
			, PRODUCT_PRICE	
			, PRODUCT_WEEKDAY_TF) 			
	VALUES (  #{userNo}
			, #{plantNo}
			, #{productName}
			, #{productType}
			, #{productContent}
			, #{productPrice} 
			, )

mapper.update:
		UPDATE 	PRODUCT 
		   SET 	PRODUCT_NAME			= #{productName}
		   	  , PLANT_NO				= #{plantNo}
		   	  , PRODUCT_TYPE			= #{productType}
		   	  , PRODUCT_PRICE 			= #{productPrice}
    	 WHERE 	USER_NO 				= #{userNo} 
    	   AND  PRODUCT_NO 				= #{productNo} 
    	   AND  PRODUCT_ACTIVE			= 1

		UPDATE 	PRODUCT 
		   SET 	PRODUCT_ACTIVE 		= 0 
    	 WHERE 	USER_NO = #{userNo} AND PRODUCT_NO = #{productNo}
