mapper.insert:
INSERT INTO 
  	   USER (  USER_ID
  			 , USER_EMAIL
  			 , USER_NM
  			 , USER_NICKNM
  			 , USER_PW
  			 , USER_BIRTHDATE
  			 , USER_TYPE 	) 			
	 VALUES (  #{userId}
	 		 , #{userEmail} 
	 		 , #{userName} 
	 		 , #{userNickname} 
	 		 , #{userPw} 
	 		 , #{userBirthdate} 
	 		 , 0 			)		
	 		 

  SELECT LAST_INSERT_ID()

mapper.update:
UPDATE	USER 
   SET	USER_PW 		= #{userPw}
 WHERE 	USER_ID 		= #{userId} 
   AND	USER_BIRTHDATE 	= #{userBirthdate}
   AND	USER_NM 		= #{userName}
   AND	USER_EMAIL		= #{userEmail}

mapper.select:
		SELECT  USER_ID
			  , USER_NM
			  , USER_NO
		  FROM 	USER 
  	 	 WHERE 	USER_ID = #{userId}
  	 	   AND 	USER_PW = #{userPw}

		SELECT  USER_EMAIL
		  FROM 	USER 
  	 	 WHERE 	USER_EMAIL = #{userEmail}

mapper.insert:
		INSERT INTO 
