INSERT INTO 
  USER_NOTI (  USER_NO
  			 , USER_PLANT_NO
  			 , USER_MILEAGE_NO
  			 , USER_NOTI_CONTENT
  			 , USER_NOTI_TYPE
  			 , USER_NOTI_ACTIVE 		) 			
	 VALUES (  #{userNo}
	 		 , #{userPlantNo} 
	 		 , #{userMileageNo} 
	 		 , #{userNotificationContent} 
	 		 , #{userNotificationType} 
	 		 , #{userNotificationActive})

mapper.update:
UPDATE	USER_NOTI 
   SET	USER_NOTI_ACTIVE 	= #{userNotificationActive},
   		USER_NOTI_CONTENT 	= #{userNotificationContent}
 WHERE 	USER_NO 			= #{userNo} 
   AND  USER_NOTI_NO 		= #{userNotificationNo}
